    public CaveMan() 
    {
        super(20 * 28, 20 * 28, 1);
        startscreen(); 
    }

    public void startscreen(){
        removeObjects(getObjects(Objects.class));
        removeObjects(getObjects(Tile.class));
        removeObjects(getObjects(endscreen.class));
        addObject(new startscreen(), 280, 280);
        started = true;

    }

    public void endscreen(){
        removeObjects(getObjects(Tile.class));
        addObject(new endscreen(), 280, 280);
        exit = true;
    }

    public void deathscreen(){
        removeObjects(getObjects(Objects.class));
        removeObjects(getObjects(Tile.class));
        removeObjects(getObjects(endscreen.class));
        addObject(new deathscreen(), 280, 280);
        started = true;
    }

    public int GenerateLevel(int num, boolean spawn)
    {
        boolean player = true;

        if(spawn == true){
            for (int f = 0 ; f < 12; f++)
            { 
                for (int i = 0 ; i < 28; i++)
                {     
                    for (int j = 0 ; j < 28; j++)
                    {
                        LVL_data[f][i][j] = LVL_data_const[f][i][j];
                    }           

                }
            }
            removeObjects(getObjects(Player.class));
            removeObjects(getObjects(startscreen.class));
            removeObjects(getObjects(endscreen.class));
            removeObjects(getObjects(deathscreen.class));
            player = false;
            return GenerateLevel(num, false);
        }

        if (num == 12){     // nur Bauen des nächsten Fensters, wenn das Level nicht zu Ende ist (12 Fenster (0,1,2,3..), also num = 12 ist das nächste Spiel)
            num = 0;
            endscreen();
            player = false;
            return 0;
            // next level 
        }

        int px = TILESIZE/2; 
        int py = TILESIZE/2;

        removeObjects(getObjects(Block.class));
        removeObjects(getObjects(Enemy.class));

        for (int i = 0 ; i < 28; i++)
        {     
            for (int j = 0 ; j < 28; j++)
            {
                int ID = LVL_data[num][i][j] ; 

                if (ID == -1)       // Spieler einfügen
                {
                    if(!player){
                        removeObjects(getObjects(Player.class));
                    }
                    addObject( Player, px, py );

                    player = true;
                }

                else if(ID == -2)
                {
                    ID = ID - 198 +  num;
                    addObject(new Block(ID), px, py);

                }
                else if(ID == -3){  // Gegner einfügen
                    addObject(new Enemy(ID), px, py);

                }

                else if(ID > 0)     // Blöcke einfügen 
                {
                    if(ID == 30) {  // wenn manublock dann passende ID 
                        ID = GetManuBlockID(num,i,j);
                    }
                    addObject( new Block(ID), px, py );     // Block nach ID einfügen

                }

                px += TILESIZE;
            }           
            px = TILESIZE/2;
            py += TILESIZE;
        }

        if(player){
            return num;
        }
        else{
            if(num == 0){

            }
            else{    
                num -= 1;    
            }

            GenerateLevel(num, false);
            return num;
        }

    }

